import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
//아래 두개 스피너
// import Loading from './../assets/Spinner';
import Loading1 from '../assets/Loading';
import { BtnDiv, Post, PostDiv } from '../../Style/PostDetailCSS';

const Detail = () => {
  const [postDetil, setPostDetail] = useState({});
  const [loaded, setLoaded] = useState(false);
  const navigate = useNavigate();
  //현재 페이지 id 때문..
  const params = useParams();
  useEffect(() => {
    // console.log(params.postNum); //post id값 ex)1
    const body = {
      //let body =
      postNum: params.postNum,
    };
    axios
      .post('/api/post/detail', body)
      .then((res) => {
        if (res.data.success) {
          setTimeout(() => {
            setPostDetail(res.data.post);
            setLoaded(true);
          }, 1000);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  useEffect(() => {
    console.log(postDetil);
  }, [postDetil]);

  const deleteHandler = () => {
    if (window.confirm('정말로 삭제하시겠습니까?')) {
      // console.log(params.postNum); //post id값 ex)1
      const body = {
        //let body =
        postNum: params.postNum,
      };
      axios
        .post('/api/post/delete', body)
        .then((res) => {
          if (res.data.success) {
            alert('게시글이 삭제되었습니다.');
            setLoaded(false);
            setTimeout(() => {
              setLoaded(true);
              navigate('/');
            }, 1000);
          }
        })
        .catch((err) => {
          alert('게시글이 삭제 실패되었습니다.');
        });
    }
  };

  return (
    <PostDiv>
      {loaded ? (
        <>
          <Post>
            <h2>{postDetil.title}</h2>
            {postDetil.image ? (
              <img
                //배포하면 배포 환경에 맞게 주소 수정해야됨
                // src={`http://localhost:5000/${postDetil.image}`}
                src={postDetil.image}
                alt="이미지"
                style={{ width: '100%', height: 'auto' }}
              />
            ) : null}

            <p>{postDetil.content}</p>
          </Post>
          <BtnDiv>
            <Link to={`/edit/${postDetil.postNum}`}>
              <button className="edit">수정</button>
            </Link>
            <Link>
              <button onClick={deleteHandler} className="delete">
                삭제
              </button>
            </Link>
          </BtnDiv>
        </>
      ) : (
        // <Loading /> //1스피너
        <Loading1 /> //2스피너
      )}
    </PostDiv>
  );
};

export default Detail;
